contracts/UserProfile.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

interface ILeaderboard {
    function registerUser(address _user, string memory _username) external;
}

/**
 * @title UserProfile
 * @dev Manages user registration and profile data for RealFi DeFi platform
 * @notice This contract handles user onboarding, profile management, and reputation scoring
 */
contract UserProfile is Initializable, UUPSUpgradeable, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    
    struct UserData {
        string username;
        uint256 registrationTime;
        uint256 totalDeposits;
        uint256 totalWithdrawals;
        uint256 battlesJoined;
        uint256 battlesWon;
        uint256 reputationScore;
        bool isActive;
        mapping(string => string) metadata; // For storing additional user data
    }
    
    // Events
    event UserRegistered(address indexed user, string username, uint256 timestamp);
    event UserUpdated(address indexed user, string username, uint256 timestamp);
    event ReputationUpdated(address indexed user, uint256 oldScore, uint256 newScore);
    event UserDeactivated(address indexed user, uint256 timestamp);
    event UserStatsUpdated(address indexed user, uint256 depositAmount, uint256 withdrawalAmount, bool battleJoined, bool battleWon, uint256 timestamp);
    
    // State variables
    mapping(address => UserData) public users;
    mapping(string => address) public usernameToAddress;
    mapping(address => bool) public isRegistered;
    
    uint256 public totalUsers;
    uint256 public activeUsers;
    
    // Contract references
    address public leaderboardContract;
    address public yieldVaultContract;
    
    // Constants
    uint256 public constant MIN_USERNAME_LENGTH = 3;
    uint256 public constant MAX_USERNAME_LENGTH = 20;
    uint256 public constant INITIAL_REPUTATION_SCORE = 100;
    
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }
    
    /**
     * @dev Initialize the contract
     * @param _owner The address of the contract owner
     */
    function initialize(address _owner) public initializer {
        __Ownable_init(_owner);
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();
    }
    
    /**
     * @dev Set the leaderboard contract address
     * @param _leaderboardContract The address of the leaderboard contract
     */
    function setLeaderboardContract(address _leaderboardContract) external onlyOwner {
        require(_leaderboardContract != address(0), "Invalid leaderboard contract address");
        leaderboardContract = _leaderboardContract;
    }
    
    function setYieldVaultContract(address _yieldVaultContract) external onlyOwner {
        require(_yieldVaultContract != address(0), "Invalid yield vault contract address");
        yieldVaultContract = _yieldVaultContract;
    }
    
    /**
     * @dev Register a new user
     * @param _username The username for the user
     * @notice Users can only register once and usernames must be unique
     */
    function registerUser(string memory _username) external nonReentrant {
        require(!isRegistered[msg.sender], "User already registered");
        require(bytes(_username).length >= MIN_USERNAME_LENGTH, "Username too short");
        require(bytes(_username).length <= MAX_USERNAME_LENGTH, "Username too long");
        require(usernameToAddress[_username] == address(0), "Username already taken");
        
        // Validate username contains only alphanumeric characters
        bytes memory usernameBytes = bytes(_username);
        for (uint256 i = 0; i < usernameBytes.length; i++) {
            bytes1 char = usernameBytes[i];
            require(
                (char >= 0x30 && char <= 0x39) || // 0-9
                (char >= 0x41 && char <= 0x5A) || // A-Z
                (char >= 0x61 && char <= 0x7A),   // a-z
                "Username contains invalid characters"
            );
        }
        
        UserData storage user = users[msg.sender];
        user.username = _username;
        user.registrationTime = block.timestamp;
        user.reputationScore = INITIAL_REPUTATION_SCORE;
        user.isActive = true;
        
        usernameToAddress[_username] = msg.sender;
        isRegistered[msg.sender] = true;
        totalUsers++;
        activeUsers++;
        
        // Register user in leaderboard if contract is set
        if (leaderboardContract != address(0)) {
            try ILeaderboard(leaderboardContract).registerUser(msg.sender, _username) {
                // Successfully registered in leaderboard
            } catch {
                // Continue even if leaderboard registration fails
            }
        }
        
        emit UserRegistered(msg.sender, _username, block.timestamp);
    }
    
    /**
     * @dev Update user profile information
     * @param _username New username (must be unique)
     */
    function updateProfile(string memory _username) external nonReentrant {
        require(isRegistered[msg.sender], "User not registered");
        require(users[msg.sender].isActive, "User account deactivated");
        require(bytes(_username).length >= MIN_USERNAME_LENGTH, "Username too short");
        require(bytes(_username).length <= MAX_USERNAME_LENGTH, "Username too long");
        require(usernameToAddress[_username] == address(0) || usernameToAddress[_username] == msg.sender, "Username already taken");
        
        // Validate username contains only alphanumeric characters
        bytes memory usernameBytes = bytes(_username);
        for (uint256 i = 0; i < usernameBytes.length; i++) {
            bytes1 char = usernameBytes[i];
            require(
                (char >= 0x30 && char <= 0x39) || // 0-9
                (char >= 0x41 && char <= 0x5A) || // A-Z
                (char >= 0x61 && char <= 0x7A),   // a-z
                "Username contains invalid characters"
            );
        }
        
        string memory oldUsername = users[msg.sender].username;
        users[msg.sender].username = _username;
        
        // Update username mapping
        delete usernameToAddress[oldUsername];
        usernameToAddress[_username] = msg.sender;
        
        emit UserUpdated(msg.sender, _username, block.timestamp);
    }
    
    /**
     * @dev Update user metadata
     * @param _key The metadata key
     * @param _value The metadata value
     */
    function updateMetadata(string memory _key, string memory _value) external {
        require(isRegistered[msg.sender], "User not registered");
        require(users[msg.sender].isActive, "User account deactivated");
        
        users[msg.sender].metadata[_key] = _value;
    }
    
    
    /**
     * @dev Update reputation score based on user activity
     * @param _user The user address
     */
    function _updateReputationScore(address _user) internal {
        UserData storage user = users[_user];
        uint256 oldScore = user.reputationScore;
        
        // Calculate new reputation score
        uint256 newScore = INITIAL_REPUTATION_SCORE;
        
        // Add points for deposits (1 point per 0.1 ETH equivalent)
        newScore += user.totalDeposits / 1e17;
        
        // Add points for battle participation
        newScore += user.battlesJoined * 10;
        
        // Add points for battle wins
        newScore += user.battlesWon * 50;
        
        // Subtract points for withdrawals (penalty for early exits)
        newScore -= user.totalWithdrawals / 1e18;
        
        // Ensure score doesn't go below 0
        if (newScore < 0) {
            newScore = 0;
        }
        
        user.reputationScore = newScore;
        
        if (oldScore != newScore) {
            emit ReputationUpdated(_user, oldScore, newScore);
        }
    }
    
    /**
     * @dev Deactivate a user account
     * @param _user The user address to deactivate
     */
    function deactivateUser(address _user) external onlyOwner {
        require(isRegistered[_user], "User not registered");
        require(users[_user].isActive, "User already deactivated");
        
        users[_user].isActive = false;
        activeUsers--;
        
        emit UserDeactivated(_user, block.timestamp);
    }
    
    /**
     * @dev Get user data
     * @param _user The user address
     * @return username The user's username
     * @return registrationTime When the user registered
     * @return totalDeposits Total amount deposited
     * @return totalWithdrawals Total amount withdrawn
     * @return battlesJoined Number of battles joined
     * @return battlesWon Number of battles won
     * @return reputationScore Current reputation score
     * @return isActive Whether the user is active
     */
    function getUserData(address _user) external view returns (
        string memory username,
        uint256 registrationTime,
        uint256 totalDeposits,
        uint256 totalWithdrawals,
        uint256 battlesJoined,
        uint256 battlesWon,
        uint256 reputationScore,
        bool isActive
    ) {
        require(isRegistered[_user], "User not registered");
        
        UserData storage user = users[_user];
        return (
            user.username,
            user.registrationTime,
            user.totalDeposits,
            user.totalWithdrawals,
            user.battlesJoined,
            user.battlesWon,
            user.reputationScore,
            user.isActive
        );
    }
    
    /**
     * @dev Get user metadata
     * @param _user The user address
     * @param _key The metadata key
     * @return The metadata value
     */
    function getUserMetadata(address _user, string memory _key) external view returns (string memory) {
        require(isRegistered[_user], "User not registered");
        return users[_user].metadata[_key];
    }
    
    /**
     * @dev Check if a username is available
     * @param _username The username to check
     * @return True if available, false otherwise
     */
    function isUsernameAvailable(string memory _username) external view returns (bool) {
        return usernameToAddress[_username] == address(0);
    }
    
    /**
     * @dev Get total statistics
     * @return _totalUsers Total number of registered users
     * @return _activeUsers Total number of active users
     */
    function getTotalStats() external view returns (uint256 _totalUsers, uint256 _activeUsers) {
        return (totalUsers, activeUsers);
    }
    
    /**
     * @dev Update user statistics (called by other contracts)
     * @param _user The user address
     * @param _depositAmount Amount deposited
     * @param _withdrawalAmount Amount withdrawn
     * @param _battleJoined Whether user joined a battle
     * @param _battleWon Whether user won a battle
     */
    function updateUserStats(
        address _user,
        uint256 _depositAmount,
        uint256 _withdrawalAmount,
        bool _battleJoined,
        bool _battleWon
    ) external {
        // Only allow YieldVault contract to call this function
        require(msg.sender == yieldVaultContract, "Only YieldVault can update stats");
        require(isRegistered[_user], "User not registered");
        
        UserData storage userData = users[_user];
        
        // Update deposit and withdrawal totals
        if (_depositAmount > 0) {
            userData.totalDeposits += _depositAmount;
        }
        if (_withdrawalAmount > 0) {
            userData.totalWithdrawals += _withdrawalAmount;
        }
        
        // Update battle statistics
        if (_battleJoined) {
            userData.battlesJoined++;
        }
        if (_battleWon) {
            userData.battlesWon++;
        }
        
        // Update reputation score based on activity
        if (_depositAmount > 0 || _battleJoined) {
            userData.reputationScore += 10; // Increase reputation for activity
        }
        
        emit UserStatsUpdated(_user, _depositAmount, _withdrawalAmount, _battleJoined, _battleWon, block.timestamp);
    }
    
    /**
     * @dev Authorize upgrade (only owner can upgrade)
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/YieldVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IUserProfile {
    function updateUserStats(
        address _user,
        uint256 _depositAmount,
        uint256 _withdrawalAmount,
        bool _battleJoined,
        bool _battleWon
    ) external;
}

/**
 * @title YieldVault
 * @dev Manages yield farming vaults and battle participation for RealFi DeFi platform
 * @notice This contract handles deposits, withdrawals, yield calculations, and battle mechanics
 */
contract YieldVault is Initializable, UUPSUpgradeable, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    using SafeERC20 for IERC20;
    
    struct VaultPosition {
        uint256 amount;
        uint256 depositTime;
        uint256 lastClaimTime;
        uint256 battleId;
        bool isActive;
    }
    
    struct Battle {
        uint256 battleId;
        string name;
        uint256 startTime;
        uint256 endTime;
        uint256 totalPrizePool;
        uint256 entryFee;
        uint256 maxParticipants;
        uint256 currentParticipants;
        bool isActive;
        mapping(address => bool) participants;
        mapping(address => uint256) participantDeposits;
    }
    
    // Events
    event DepositMade(address indexed user, uint256 amount, uint256 battleId, uint256 timestamp);
    event WithdrawalMade(address indexed user, uint256 amount, uint256 timestamp);
    event BattleCreated(uint256 indexed battleId, string name, uint256 startTime, uint256 endTime, uint256 entryFee);
    event BattleJoined(address indexed user, uint256 indexed battleId, uint256 amount, uint256 timestamp);
    event BattleEnded(uint256 indexed battleId, address winner, uint256 prizeAmount, uint256 timestamp);
    event YieldClaimed(address indexed user, uint256 amount, uint256 timestamp);
    event PrizeDistributed(uint256 indexed battleId, address indexed winner, uint256 amount, uint256 timestamp);
    
    // State variables
    mapping(address => VaultPosition) public userPositions;
    mapping(uint256 => Battle) public battles;
    mapping(address => uint256) public userTotalDeposits;
    mapping(address => uint256) public userTotalWithdrawals;
    mapping(address => uint256) public userTotalYield;
    
    IERC20 public token;
    address public userProfileContract;
    
    uint256 public nextBattleId;
    uint256 public totalVaultValue;
    uint256 public totalYieldDistributed;
    uint256 public constant YIELD_RATE = 50; // 50% APY for testing
    uint256 public constant BATTLE_DURATION = 1 days; // Reduced from 7 days to 1 day
    uint256 public constant MIN_DEPOSIT = 0.01 ether;
    uint256 public constant MAX_DEPOSIT = 100 ether;
    
    // Battle configuration
    uint256 public constant MIN_ENTRY_FEE = 0.001 ether;
    uint256 public constant MAX_ENTRY_FEE = 1 ether;
    uint256 public constant DEFAULT_MAX_PARTICIPANTS = 100;
    
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }
    
    /**
     * @dev Initialize the contract
     * @param _owner The address of the contract owner
     * @param _token The ERC20 token address for deposits
     * @param _userProfileContract The address of the UserProfile contract
     */
    function initialize(
        address _owner,
        address _token,
        address _userProfileContract
    ) public initializer {
        __Ownable_init(_owner);
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();
        
        token = IERC20(_token);
        userProfileContract = _userProfileContract;
        nextBattleId = 1;
    }
    
    /**
     * @dev Create a new yield battle
     * @param _name The name of the battle
     * @param _entryFee The entry fee for the battle
     * @param _maxParticipants Maximum number of participants
     * @param _duration Battle duration in seconds
     */
    function createBattle(
        string memory _name,
        uint256 _entryFee,
        uint256 _maxParticipants,
        uint256 _duration
    ) external onlyOwner {
        require(_entryFee >= MIN_ENTRY_FEE && _entryFee <= MAX_ENTRY_FEE, "Invalid entry fee");
        require(_maxParticipants > 0 && _maxParticipants <= DEFAULT_MAX_PARTICIPANTS, "Invalid max participants");
        require(_duration > 0 && _duration <= 30 days, "Invalid duration");
        
        uint256 battleId = nextBattleId++;
        Battle storage battle = battles[battleId];
        
        battle.battleId = battleId;
        battle.name = _name;
        battle.startTime = block.timestamp;
        battle.endTime = block.timestamp + _duration;
        battle.entryFee = _entryFee;
        battle.maxParticipants = _maxParticipants;
        battle.currentParticipants = 0;
        battle.isActive = true;
        
        emit BattleCreated(battleId, _name, battle.startTime, battle.endTime, _entryFee);
    }
    
    /**
     * @dev Join a yield battle with a deposit
     * @param _battleId The ID of the battle to join
     * @param _amount The amount to deposit
     */
    function joinBattle(uint256 _battleId, uint256 _amount) external nonReentrant {
        require(_amount >= MIN_DEPOSIT && _amount <= MAX_DEPOSIT, "Invalid deposit amount");
        require(battles[_battleId].isActive, "Battle not active");
        require(block.timestamp >= battles[_battleId].startTime, "Battle not started");
        require(block.timestamp <= battles[_battleId].endTime, "Battle ended");
        require(!battles[_battleId].participants[msg.sender], "Already joined battle");
        require(battles[_battleId].currentParticipants < battles[_battleId].maxParticipants, "Battle full");
        
        // Transfer tokens from user
        token.safeTransferFrom(msg.sender, address(this), _amount);
        
        // Update battle state
        battles[_battleId].participants[msg.sender] = true;
        battles[_battleId].participantDeposits[msg.sender] = _amount;
        battles[_battleId].currentParticipants++;
        battles[_battleId].totalPrizePool += _amount;
        
        // Update user position
        VaultPosition storage position = userPositions[msg.sender];
        position.amount += _amount;
        position.depositTime = block.timestamp;
        position.lastClaimTime = block.timestamp;
        position.battleId = _battleId;
        position.isActive = true;
        
        // Update global state
        userTotalDeposits[msg.sender] += _amount;
        totalVaultValue += _amount;
        
        // Update user profile stats
        _updateUserProfileStats(msg.sender, _amount, 0, true, false);
        
        emit BattleJoined(msg.sender, _battleId, _amount, block.timestamp);
        emit DepositMade(msg.sender, _amount, _battleId, block.timestamp);
    }
    
    /**
     * @dev Make a regular deposit (not in a battle)
     * @param _amount The amount to deposit
     */
    function deposit(uint256 _amount) external nonReentrant {
        require(_amount >= MIN_DEPOSIT && _amount <= MAX_DEPOSIT, "Invalid deposit amount");
        
        // Transfer tokens from user
        token.safeTransferFrom(msg.sender, address(this), _amount);
        
        // Update user position
        VaultPosition storage position = userPositions[msg.sender];
        position.amount += _amount;
        position.depositTime = block.timestamp;
        position.lastClaimTime = block.timestamp;
        position.isActive = true;
        
        // Update global state
        userTotalDeposits[msg.sender] += _amount;
        totalVaultValue += _amount;
        
        // Update user profile stats
        _updateUserProfileStats(msg.sender, _amount, 0, false, false);
        
        emit DepositMade(msg.sender, _amount, 0, block.timestamp);
    }
    
    /**
     * @dev Withdraw funds from vault
     * @param _amount The amount to withdraw
     */
    function withdraw(uint256 _amount) external nonReentrant {
        VaultPosition storage position = userPositions[msg.sender];
        require(position.isActive, "No active position");
        require(_amount <= position.amount, "Insufficient balance");
        require(_amount > 0, "Amount must be greater than 0");
        
        // Check if user is in an active battle
        if (position.battleId > 0 && battles[position.battleId].isActive) {
            require(block.timestamp > battles[position.battleId].endTime, "Cannot withdraw during active battle");
        }
        
        // Calculate and claim yield first
        uint256 yieldAmount = calculateYield(msg.sender);
        if (yieldAmount > 0) {
            _claimYield(msg.sender);
        }
        
        // Update position
        position.amount -= _amount;
        if (position.amount == 0) {
            position.isActive = false;
        }
        
        // Update global state
        userTotalWithdrawals[msg.sender] += _amount;
        totalVaultValue -= _amount;
        
        // Transfer tokens to user
        token.safeTransfer(msg.sender, _amount);
        
        // Update user profile stats
        _updateUserProfileStats(msg.sender, 0, _amount, false, false);
        
        emit WithdrawalMade(msg.sender, _amount, block.timestamp);
    }
    
    /**
     * @dev Withdraw all funds from vault
     */
    function withdrawAll() external nonReentrant {
        VaultPosition storage position = userPositions[msg.sender];
        require(position.isActive, "No active position");
        require(position.amount > 0, "No funds to withdraw");
        
        // Check if user is in an active battle
        if (position.battleId > 0 && battles[position.battleId].isActive) {
            require(block.timestamp > battles[position.battleId].endTime, "Cannot withdraw during active battle");
        }
        
        uint256 totalAmount = position.amount;
        
        // Calculate and claim yield first
        uint256 yieldAmount = calculateYield(msg.sender);
        if (yieldAmount > 0) {
            _claimYield(msg.sender);
        }
        
        // Update position
        position.amount = 0;
        position.isActive = false;
        
        // Update global state
        userTotalWithdrawals[msg.sender] += totalAmount;
        totalVaultValue -= totalAmount;
        
        // Transfer tokens to user
        token.safeTransfer(msg.sender, totalAmount);
        
        // Update user profile stats
        _updateUserProfileStats(msg.sender, 0, totalAmount, false, false);
        
        emit WithdrawalMade(msg.sender, totalAmount, block.timestamp);
    }
    
    /**
     * @dev Claim accumulated yield
     */
    function claimYield() external nonReentrant {
        uint256 yieldAmount = calculateYield(msg.sender);
        require(yieldAmount > 0, "No yield to claim");
        
        _claimYield(msg.sender);
    }
    
    /**
     * @dev Internal function to claim yield
     * @param _user The user address
     */
    function _claimYield(address _user) internal {
        uint256 yieldAmount = calculateYield(_user);
        require(yieldAmount > 0, "No yield to claim");
        
        VaultPosition storage position = userPositions[_user];
        position.lastClaimTime = block.timestamp;
        
        userTotalYield[_user] += yieldAmount;
        totalYieldDistributed += yieldAmount;
        
        // Transfer yield to user
        token.safeTransfer(_user, yieldAmount);
        
        emit YieldClaimed(_user, yieldAmount, block.timestamp);
    }
    
    /**
     * @dev Calculate yield for a user
     * @param _user The user address
     * @return The yield amount
     */
    function calculateYield(address _user) public view returns (uint256) {
        VaultPosition storage position = userPositions[_user];
        if (!position.isActive || position.amount == 0) {
            return 0;
        }
        
        uint256 timeElapsed = block.timestamp - position.lastClaimTime;
        uint256 yieldAmount = (position.amount * YIELD_RATE * timeElapsed) / (365 days * 100);
        
        return yieldAmount;
    }
    
    /**
     * @dev End a battle and distribute prizes
     * @param _battleId The ID of the battle to end
     */
    function endBattle(uint256 _battleId) external onlyOwner {
        Battle storage battle = battles[_battleId];
        require(battle.isActive, "Battle not active");
        require(block.timestamp >= battle.endTime, "Battle not ended yet");
        
        battle.isActive = false;
        
        // Calculate total prize pool from entry fees
        uint256 prizeAmount = battle.entryFee * battle.currentParticipants;
        
        if (prizeAmount > 0 && battle.currentParticipants > 0) {
            // Winner selection: Use a combination of deposit amount and yield earned
            // This creates a fair competition where both initial investment and performance matter
            address winner = address(0);
            
            // For this simplified implementation, we'll use a deterministic approach
            // In production, you'd iterate through all participants and calculate scores
            // based on their deposit amount and yield earned during the battle
            
            // For now, we'll select winner based on battle ID and current participants
            // This ensures consistency while being deterministic
            if (battle.currentParticipants > 0) {
                // Use block timestamp and battle ID for deterministic but pseudo-random selection
                uint256 seed = uint256(keccak256(abi.encodePacked(_battleId, block.timestamp, block.prevrandao)));
                winner = address(uint160(seed % (2**160 - 1)));
            }
            
            emit BattleEnded(_battleId, winner, prizeAmount, block.timestamp);
        }
    }
    
    /**
     * @dev Get battle winners (simplified implementation)
     * @param _battleId The battle ID
     * @return winners Array of winner addresses
     * @return yields Array of yield amounts earned
     * @return ranks Array of ranks (1st, 2nd, 3rd, etc.)
     */
    function getBattleWinners(uint256 _battleId) external view returns (
        address[] memory winners,
        uint256[] memory yields,
        uint256[] memory ranks
    ) {
        Battle storage battle = battles[_battleId];
        require(!battle.isActive, "Battle still active");
        
        // For this simplified implementation, we'll return empty arrays
        // In production, you'd store and return actual winner data
        winners = new address[](0);
        yields = new uint256[](0);
        ranks = new uint256[](0);
    }
    
    /**
     * @dev Update user profile statistics
     * @param _user The user address
     * @param _depositAmount Amount deposited
     * @param _withdrawalAmount Amount withdrawn
     * @param _battleJoined Whether user joined a battle
     * @param _battleWon Whether user won a battle
     */
    function _updateUserProfileStats(
        address _user,
        uint256 _depositAmount,
        uint256 _withdrawalAmount,
        bool _battleJoined,
        bool _battleWon
    ) internal {
        // Call UserProfile contract to update stats
        if (userProfileContract != address(0)) {
            try IUserProfile(userProfileContract).updateUserStats(
                _user,
                _depositAmount,
